-- View structure for be_like_article_view
-- ----------------------------
DROP VIEW IF EXISTS `be_like_article_view`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `be_like_article_view` AS select count(0) AS `was_like_count`,`users`.`user_name` AS `user_name` from ((`article_like` join `articles` on((`article_like`.`like_article_id` = `articles`.`id`))) join `users` on((`articles`.`author_id` = `users`.`id`))) group by `articles`.`author_id`;

