delimiter ;;
CREATE FUNCTION `getArticleCommentCount`(`article_id` int)
 RETURNS int
  READS SQL DATA 
BEGIN
	DECLARE result INT;
	SELECT COUNT(*) INTO result FROM article_message WHERE article_message.article_id = article_id;
	RETURN result;
END
;;
delimiter ;

